import React, { useState, useEffect } from 'react';
import img from './cold.png'
import img2 from './mild.png'
import img3 from './sunny.png'

const WeatherComponent = () => {
  const [weatherData, setWeatherData] = useState(null);
  const [isCelsius, setIsCelsius] = useState(true);

  useEffect(() => {

    const apiKey = 'a256f84b5a84a1431164d0ed7eab26ab';
    const city = 'Halifax'; // Replace with your ci
    const apiUrl = `https://api.openweathermap.org/data/2.5/weather?q=${city}&appid=${apiKey}&units=metric`;

    fetch(apiUrl)
      .then((response) => response.json())
      .then((data) => setWeatherData(data))
      .catch((error) => console.error('Error fetching data:', error));
  }, []);

  const toggleTemperatureUnit = () => {
    setIsCelsius(prev => !prev);
  }


  if (!weatherData) {
    return <div>Loading...</div>;
  }

  const temperature = isCelsius ? weatherData.main.temp : (weatherData.main.temp * 9/5) + 32;
  let weatherIcon;

  if (temperature <= 10) {
    weatherIcon = img
  } else if (temperature > 10 && temperature < 20) {
    weatherIcon = img2
  } else {
    weatherIcon = img3
  }

//   setImage(weatherIcon);

//   const [image, setImage] = useState(img);

//   return (
//     <div>
//       <h2>Current Weather in {weatherData.name}</h2>
//       <img src={weatherIcon} alt="Weather Icon" />
//       <p>{temperature}°C</p>
//     </div>
//   );

  return (
    <div>
      <div>
        <button onClick={toggleTemperatureUnit}>
          Change to {isCelsius ? '°F' : '°C'}
        </button>
      </div>
      <div>
      <img src={weatherIcon} alt="Weather Icon" />
        Temperature: {temperature} {isCelsius ? '°C' : '°F'}
      </div>
      {/* Render weather information here */}
    </div>
  );

};

export default WeatherComponent;
