import React, { useState, useEffect } from 'react';
import cold from './cold.png'
import mild from './mild.png'
import sunny from './sunny.png'

const WeatherSection = () => {
  const [weatherData, setWeatherData] = useState(null);
  const [isDegree, setIsDegree] = useState(true);

  useEffect(() => {

    const apiKey = 'ccd4a7de04a58dd377790678f4d65339';
    const town = 'Halifax';
    const apiUrl = `https://api.openweathermap.org/data/2.5/weather?q=${town}&appid=${apiKey}&units=metric`;

    fetch(apiUrl)
      .then((response) => response.json())
      .then((data) => setWeatherData(data))
      .catch((error) => console.error('Error fetching data:', error));
  }, []);

  const toggleTemperatureUnit = () => {
    setIsCelsius(prev => !prev);
  }


  if (!weatherData) {
    return <div>Loading...</div>;
  }

  const temperature = isCelsius ? weatherData.main.temp : (weatherData.main.temp * 9/5) + 32;
  let weatherIcon;

  if (temperature <= 10) {
    weatherIcon = cold
  } else if (temperature > 10 && temperature < 20) {
    weatherIcon = mild
  } else {
    weatherIcon = sunny
  }


  return (
    <div>
        <div style={{display: 'flex'}}>
            <h4>{temperature} {isCelsius ? '°C' : '°F'}</h4> 
            <img src={weatherIcon} alt="Weather Icon" width='80px' />
        </div>
        <div>
            <button onClick={toggleTemperatureUnit}>
            Click here to change to {isCelsius ? '°F' : '°C'}
            </button>
        </div>

    </div>
  );

};

export default WeatherSection;
