import React, { useState, useEffect } from 'react';
import cold from './cold.png'
import mild from './mild.png'
import sunny from './sunny.png'

const WeatherSection = () => {
  const [weatherData, setWeatherData] = useState(null);
  const [isCelsius, setIsCelsius] = useState(true);
  const [temperature, setTemperature] = useState(null);

  useEffect(() => {

    const apiKey = 'a256f84b5a84a1431164d0ed7eab26ab';
    const city = 'Toronto';
    const apiUrl = `https://api.openweathermap.org/data/2.5/weather?q=${city}&appid=${apiKey}&units=metric`;

    fetch(apiUrl)
      .then((response) => response.json())
      .then((data) => {
        setWeatherData(data)
        const temperature = isCelsius ? data.main.temp : (data.main.temp * 9/5) + 32;
        setTemperature(temp);
      })
      .catch((error) => console.error('Error fetching data:', error));
  }, [isCelsius]);

  const toggleTemperatureUnit = () => {
    setIsCelsius(prev => !prev);
  }


  if (!weatherData) {
    return <div>Loading...</div>;
  }

//   const temperature = isCelsius ? weatherData.main.temp : (weatherData.main.temp * 9/5) + 32;
  let weatherIcon;

  if (temperature <= 10) {
    weatherIcon = cold
  } else if (temperature > 10 && temperature < 20) {
    weatherIcon = mild
  } else {
    weatherIcon = sunny
  }


  return (
    <div>
      <div>
        <button onClick={toggleTemperatureUnit}>
          Change to {isCelsius ? '°F' : '°C'}
        </button>
      </div>
      <div>
      <img src={weatherIcon} alt="Weather Icon" />
        Temperature: {temperature} {isCelsius ? '°C' : '°F'}
      </div>
      {/* Render weather information here */}
    </div>
  );

};

export default WeatherSection;
